package tyep_casting;

public class conversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		System.out.println("num="+num);
		double d=num;
		System.out.println("d="+d);
		float a=(float)num;
		System.out.println("floaat="+a);
	}

}
//convert larger size into smaller size is narrowing or emplicit converion(done by manually)
//convert smaller type to larger type size is widening or implic conversion(done by automatically)